INSERT INTO `youjidiv` VALUES ('19', '说说这次旅行', '<div class=\"e_img_schedule\">\r\n	<div class=\"imglst\">\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				很快又要到中秋佳节了，\r\n			</p>\r\n			<p>\r\n				中秋正值月圆之时，赏月是必不可少的传统中秋项目。\r\n			</p>\r\n			<p>\r\n				如果能够在小长假玩耍放松的同时轻松欣赏明月，想必是再好不过的了！\r\n			</p>\r\n			<p>\r\n				本文会推荐给大家一些<a href=\"http://travel.qunar.com/p-cs299914-beijing\" target=\"_blank\" class=\"link_dest\">北京</a>适合与朋友家人游玩时赏月的地点~\r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', '9', '');
