INSERT INTO `youjidiv` VALUES ('33', '主题花坛', '<div class=\"e_img_schedule\">\r\n	<div class=\"imglst\">\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				2018<a href=\"http://travel.qunar.com/p-cs299914-beijing\" target=\"_blank\" class=\"link_dest\">北京</a>市属公园中秋主题花坛抢先看\r\n			</p>\r\n		</div>\r\n<img title=\"北京图片\" alt=\"北京图片\" class=\"box_img js_box_img js_lazyimg\" src=\"https://img1.qunarzz.com/travel/d3/1809/47/28c9dbd7b586d5b5.jpg_r_680x453x95_9afcd247.jpg\" width=\"680px\" height=\"453px\" /><span class=\"tri\"></span>\r\n		<div class=\"img_txt\">\r\n			<p>\r\n				图片来自网络\r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', '10', '北京');
INSERT INTO `youjidiv` VALUES ('34', '', '<div class=\"e_img_schedule\">\r\n	<div class=\"imglst\">\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				<a href=\"http://travel.qunar.com/p-oi713993-taorantinggongyuan\" target=\"_blank\" class=\"link_dest\">陶然亭公园</a>于东门广场布置主题花坛“祝福”，同时设计东门沿线造型艺菊展示及南线花街2条赏花路线。此外，本次环境布置注重与乡土植物的巧妙结合，在公园东门主花坛、园内地栽花境内，栽植和摆放小菊、串红、切花菊等花期适宜、易于养护的乡土花卉近万盆。\r\n			</p>\r\n		</div>\r\n<img title=\"陶然亭公园图片\" alt=\"陶然亭公园图片\" class=\"box_img js_box_img js_lazyimg\" src=\"https://img1.qunarzz.com/travel/d9/1809/98/b2bac88dcd1336b5.jpg_r_680x449x95_2609bcd5.jpg\" width=\"680px\" height=\"449px\" /><span class=\"tri\"></span>\r\n		<div class=\"img_txt\">\r\n			<p>\r\n				图片来自网络\r\n			</p>\r\n		</div>\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				“祝福”主题花坛：整座花坛像一张翻开的“立体贺卡”，象征着对祖国生日的祝福之情。花坛主景为一组立体花篮造型，花篮四周采用四季海棠、小菊、金叶反曲景天等植物材料进行扦插装饰。左侧为一组灯笼造型，象征祖国发展蒸蒸日上。使用四季海棠、红草、绿草、黄小菊、粉小菊、串红、一品红等各色花卉共计5000余盆，占地120平方米。\r\n			</p>\r\n		</div>\r\n<img title=\"陶然亭公园图片\" alt=\"陶然亭公园图片\" class=\"box_img js_box_img js_lazyimg\" src=\"https://img1.qunarzz.com/travel/d3/1809/7c/c3ff54452568eeb5.jpg_r_680x380x95_1f742d56.jpg\" width=\"680px\" height=\"380px\" /><span class=\"tri\"></span>\r\n		<div class=\"img_txt\">\r\n			<p>\r\n				图片来自网络\r\n			</p>\r\n		</div>\r\n<img title=\"陶然亭公园图片\" alt=\"陶然亭公园图片\" class=\"box_img js_box_img js_lazyimg\" src=\"https://img1.qunarzz.com/travel/d2/1809/4b/cd37c28eb23658b5.jpg_r_680x509x95_26398566.jpg\" width=\"680px\" height=\"509px\" /><span class=\"tri\"></span>\r\n		<div class=\"img_txt\">\r\n			<p>\r\n				图片来自去哪儿聪明旅行家@梁lsh\r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', '10', '陶然亭公园');
INSERT INTO `youjidiv` VALUES ('35', '', '<div class=\"e_img_schedule\">\r\n	<div class=\"imglst\">\r\n		<img title=\"颐和园图片\" alt=\"颐和园图片\" class=\"box_img js_box_img js_lazyimg\" src=\"https://img1.qunarzz.com/travel/d3/1809/8e/d6c76fe33a5517b5.jpg_r_680x453x95_7f7464b4.jpg\" width=\"680px\" height=\"453px\" />\r\n		<div class=\"img_btn_wrap\">\r\n			<span class=\"img_btn img_btn_digg iconfont\"><span class=\"num\"></span></span><span class=\"img_btn img_btn_comment iconfont\"></span>\r\n		</div>\r\n<span class=\"tri\"></span>\r\n		<div class=\"img_txt\">\r\n			<p>\r\n				图片来自去哪儿用户@男丨子\r\n			</p>\r\n		</div>\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				<a href=\"http://travel.qunar.com/p-oi710431-yiheyuan\" target=\"_blank\" class=\"link_dest\">颐和园</a>正值第17届“颐和秋韵”桂花文化节，共计设置立体景观文化花坛2处：国花台“松鹤延年”和<a href=\"http://travel.qunar.com/p-oi9772966-wenchangyuan\" target=\"_blank\" class=\"link_dest\">文昌院</a>“古韵颐和”;北如意门地栽花带2处，<a href=\"http://travel.qunar.com/p-oi9773024-zhanglang\" target=\"_blank\" class=\"link_dest\">长廊</a>沿线和主要游览区、门区展摆<a href=\"http://travel.qunar.com/p-oi710431-yiheyuan\" target=\"_blank\" class=\"link_dest\">颐和园</a>特色花缸91个、大型紫薇盆景17盆、大型盆栽桂花154盆。\r\n			</p>\r\n		</div>\r\n<img title=\"颐和园图片\" alt=\"颐和园图片\" class=\"box_img js_box_img js_lazyimg\" src=\"https://img1.qunarzz.com/travel/d2/1809/c0/1e0375b08f11d9b5.jpg_r_680x452x95_56a61460.jpg\" width=\"680px\" height=\"452px\" /><span class=\"tri\"></span>\r\n		<div class=\"img_txt\">\r\n			<p>\r\n				图片来自去哪儿用户@秋水影像\r\n			</p>\r\n		</div>\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				“松鹤延年”主题花坛：以古建彩画元素“包袱”为主体造型，以立体形式展现中国彩画之精美。造型以仙鹤、松树为主体，寓意如松鹤般高洁、长寿。花坛总高4.5米，长15.5米，立体面积约48平方米，平面面积约119平方米。\r\n			</p>\r\n		</div>\r\n<img title=\"颐和园图片\" alt=\"颐和园图片\" class=\"box_img js_box_img js_lazyimg\" src=\"https://img1.qunarzz.com/travel/d5/1809/39/c772bd4a694d0db5.jpg_r_680x453x95_f674a4ba.jpg\" width=\"680px\" height=\"453px\" /><span class=\"tri\"></span>\r\n		<div class=\"img_txt\">\r\n			<p>\r\n				图片来自去哪儿用户@缠丝追影\r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', '10', '颐和园');
INSERT INTO `youjidiv` VALUES ('36', '', '<div class=\"e_img_schedule\">\r\n	<div class=\"imglst\">\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				<a href=\"http://travel.qunar.com/p-oi703209-zhongshangongyuan\" target=\"_blank\" class=\"link_dest\">中山公园</a>以“古坛溢彩迎国庆”为主题，突出中山整体花卉环境的布置。结合<a href=\"http://travel.qunar.com/p-oi703209-zhongshangongyuan\" target=\"_blank\" class=\"link_dest\">中山公园</a>的国庆赏花特色，突出配合展示大丽花、兰花、叶子花、桂花等公园自养的特色花卉，提升公园的整体环境，营造喜庆、热烈的节日氛围。\r\n			</p>\r\n		</div>\r\n<img title=\"中山公园图片\" alt=\"中山公园图片\" class=\"box_img js_box_img js_lazyimg\" src=\"https://img1.qunarzz.com/travel/d0/1809/f2/a3e26095ec40a4b5.jpg_r_680x453x95_30de80a4.jpg\" width=\"680px\" height=\"453px\" /><span class=\"tri\"></span>\r\n		<div class=\"img_txt\">\r\n			<p>\r\n				图片来自去哪儿聪明旅行家@梁lsh\r\n			</p>\r\n		</div>\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				“六合同春”主题花坛：花坛为五色草和四季海棠插制的中国传统吉祥图案“鹿鹤同春”造型。“鹿”与“六”谐音，“鹤”与“合”谐音，“梧桐”借“桐”、“同”谐音。“六合”为天、地、四方，泛指天下。“六合同春”指天下万物欣欣向荣，寓意在中国共*党的领导下，人民生活幸福美满、伟大祖国和谐繁荣。使用四季海棠、五色草、金叶佛甲草、大丽花、菊花、叶子花等4.5万株，平面面积约65平方米。\r\n			</p>\r\n		</div>\r\n<img title=\"中山公园图片\" alt=\"中山公园图片\" class=\"box_img js_box_img js_lazyimg\" src=\"https://img1.qunarzz.com/travel/d5/1809/98/e3c6057ada083cb5.jpg_r_680x399x95_67973d7b.jpg\" width=\"680px\" height=\"399px\" /><span class=\"tri\"></span>\r\n		<div class=\"img_txt\">\r\n			<p>\r\n				图片来自网络\r\n			</p>\r\n		</div>\r\n<img title=\"中山公园图片\" alt=\"中山公园图片\" class=\"box_img js_box_img js_lazyimg\" src=\"https://img1.qunarzz.com/travel/d5/1809/86/ac128dcfd77aebb5.jpg_r_680x338x95_ce17f3bf.jpg\" width=\"680px\" height=\"338px\" /><span class=\"tri\"></span>\r\n		<div class=\"img_txt\">\r\n			<p>\r\n				图片来自去哪儿用户@cici3475\r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', '10', '中山公园');
INSERT INTO `youjidiv` VALUES ('37', '放灯', '<div class=\"e_img_schedule\">\r\n	<div class=\"imglst\">\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				放孔明灯的地方比较少\r\n			</p>\r\n			<p>\r\n				现在市里都禁止了，有些郊区可以，但也要经过管理员的允许后才能放\r\n			</p>\r\n			<p>\r\n				前些日子去的古北水镇，晚上看夜景时，看到有放孔明灯的\r\n			</p>\r\n			<p>\r\n				不过，小编觉得，在这树木茂盛的山里，是不应该放的······\r\n			</p>\r\n			<p>\r\n				咱不放好吧，把钱攒起来，去吃炸鸡、喝啤酒好吧，这个会更环保吧\r\n			</p>\r\n			<p>\r\n				要不咱就在风筝上装个灯，然后放？\r\n			</p>\r\n			<p>\r\n				就是要放的话，我也拦不住，那就推荐两个最美的乡镇去放灯吧\r\n			</p>\r\n			<p>\r\n				而且这两个地方节味儿很浓的哦\r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', '10', '');
