INSERT INTO `youjidiv` VALUES ('41', '玩兔儿爷', '<div class=\"e_img_schedule\">\r\n	<div class=\"imglst\">\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				兔儿爷是<a href=\"http://travel.qunar.com/p-cs299914-beijing\" target=\"_blank\" class=\"link_dest\">北京</a>市的地方传统手工艺品，属于中秋应节应令的儿童玩具。人们按照月宫里有嫦娥玉兔的说法，把玉兔进一步艺术化、人格化，乃至神化之后，用泥巴塑造成各种不同形式的兔儿爷。它渊源于古老的月亮崇拜。明清以来，月宫玉兔逐渐从月崇拜的附属物中分离出来，在祭月仪式中形成了独立的形象，并逐渐丰富。兔儿爷兼具神圣和世俗的品性，融祭祀和游乐的功能于一体。如今兔儿爷已经成为最具代表性的<a href=\"http://travel.qunar.com/p-cs299914-beijing\" target=\"_blank\" class=\"link_dest\">北京</a>非物质文化遗产之一。&nbsp;\r\n			</p>\r\n		</div>\r\n<img title=\"爨底下图片\" alt=\"爨底下图片\" class=\"box_img js_box_img js_lazyimg\" src=\"https://img1.qunarzz.com/travel/d8/1809/dc/06d66001d5f4f6b5.gif\" width=\"500px\" height=\"419px\" /><span class=\"tri\"></span>\r\n		<div class=\"img_txt\">\r\n			<p>\r\n				图片来自网络\r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', '10', '');
