INSERT INTO `youjidiv` VALUES ('307', '', '<p>\r\n	<a href=\"http://travel.qunar.com/p-cs299979-chongqing\" target=\"_blank\" class=\"link_dest\">重庆</a>，是个最近出镜率很高的名字，从《火锅英雄》里的市井气，到《从你的全世界路过》里的小清新，这个中国内陆的神奇城市，近期可能越来越被关注。\r\n</p>\r\n<p>\r\n	<div class=\"e_img_schedule\">\r\n		<div class=\"imglst\">\r\n			<div class=\"text js_memo_node\">\r\n				<p>\r\n					我对<a href=\"http://travel.qunar.com/p-cs299979-chongqing\" target=\"_blank\" class=\"link_dest\">重庆</a>的向往，不仅仅是因为他的麻辣味道，更多的可能是因为这是一个人文气很重的地方，尤其在国庆节这样一个人潮爆炸的时间，还能游刃有余的探寻<a href=\"http://travel.qunar.com/p-cs299979-chongqing\" target=\"_blank\" class=\"link_dest\">重庆</a>的秘境之地，实在难得！\r\n				</p>\r\n			</div>\r\n<img title=\"说说这次旅行图片\" alt=\"说说这次旅行图片\" class=\"box_img js_box_img js_lazyimg\" src=\"https://img1.qunarzz.com/travel/d6/1610/e4/9bf3f9cbe2ca63b5.jpg_r_680x453x95_8de9bd9e.jpg\" width=\"680px\" height=\"453px\" />\r\n		</div>\r\n	</div>\r\n	<div class=\"ops\">\r\n		<div class=\"inner clrfix\">\r\n			<div class=\"left\">\r\n			</div>\r\n		</div>\r\n	</div>\r\n</p>', '19', '说说这次旅行');
