INSERT INTO `youjidiv` VALUES ('389', '', '<div class=\"e_img_schedule\">\r\n	<div class=\"imglst\">\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				自驾线路：\r\n			</p>\r\n			<p>\r\n				太原——沕沕水&nbsp;（150公里）\r\n			</p>\r\n			<p>\r\n				太原→阳曲→寿阳→南楼→盂县→交口→仙人乡→东庄头→清风→塔崖→沕沕水\r\n			</p>\r\n			<p>\r\n				阳泉——沕沕水&nbsp;（75公里）\r\n			</p>\r\n			<p>\r\n				阳泉→盂县→交口→仙人乡→东庄头→清风→塔崕→沕沕水\r\n			</p>\r\n			<p>\r\n				<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>——沕沕水&nbsp;（95公里）\r\n			</p>\r\n			<p>\r\n				<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>→北二环→石闫公路→百尺杆→<a href=\"http://travel.qunar.com/p-cs298944-pingshan\" target=\"_blank\" class=\"link_dest\">平山</a>→温塘→古月（左转）→沕沕水\r\n			</p>\r\n			<p>\r\n				<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>——沕沕水（高速线路）\r\n			</p>\r\n			<p>\r\n				西柏坡高速→温塘（下高速）→古月（左转）→沕沕水\r\n			</p>\r\n			<p>\r\n				北京——沕沕水\r\n			</p>\r\n			<p>\r\n				京昆高速→西柏坡高速→温塘（下高速）→古月（左转）→沕沕水\r\n			</p>\r\n			<p>\r\n				保定→沕沕水&nbsp;（200多公里）\r\n			</p>\r\n			<p>\r\n				京昆高速→西柏坡高速→温塘（下高速）→古月（左转）→沕沕水\r\n			</p>\r\n			<p>\r\n				天津——沕沕水&nbsp;（400多公里）\r\n			</p>\r\n			<p>\r\n				津保高速→京昆高速→西柏坡高速→温塘（下高速）→古月（左转）→沕沕水\r\n			</p>\r\n			<p>\r\n				班车线路：\r\n			</p>\r\n			<p>\r\n				<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>客运北站——<a href=\"http://travel.qunar.com/p-cs298944-pingshan\" target=\"_blank\" class=\"link_dest\">平山</a>县城，每十分钟一班。\r\n			</p>\r\n			<p>\r\n				<a href=\"http://travel.qunar.com/p-cs298944-pingshan\" target=\"_blank\" class=\"link_dest\">平山</a>县城——沕沕水，上午8:30，&nbsp;下午1:20。一天中有两趟车到沕沕水景区。\r\n			</p>\r\n			<p>\r\n				<a href=\"http://travel.qunar.com/p-cs298944-pingshan\" target=\"_blank\" class=\"link_dest\">平山</a>县城有出租车和私家车到沕沕水景区，最多100元，但回城车不好打，只能坐班车。\r\n			</p>\r\n			<p>\r\n				沕沕水——<a href=\"http://travel.qunar.com/p-cs298944-pingshan\" target=\"_blank\" class=\"link_dest\">平山</a>县城，上午6:20&nbsp;，下午1:20。一天中有两趟车返回<a href=\"http://travel.qunar.com/p-cs298944-pingshan\" target=\"_blank\" class=\"link_dest\">平山</a>县城。\r\n			</p>\r\n			<p>\r\n				<a href=\"http://travel.qunar.com/p-cs298944-pingshan\" target=\"_blank\" class=\"link_dest\">平山</a>县城——<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>客运北站，每十分钟一班。\r\n			</p>\r\n			<p>\r\n				<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>客运北站——沕沕水旅游专线车，每天8:30，有往返加门票套餐。\r\n			</p>\r\n			<p>\r\n				太原客运总站——<a href=\"http://travel.qunar.com/p-cs298944-pingshan\" target=\"_blank\" class=\"link_dest\">平山</a>客运站\r\n			</p>\r\n			<p>\r\n				北京莲花池车站——<a href=\"http://travel.qunar.com/p-cs298944-pingshan\" target=\"_blank\" class=\"link_dest\">平山</a>客运站\r\n			</p>\r\n			<p>\r\n				天津红桥客运站——<a href=\"http://travel.qunar.com/p-cs298944-pingshan\" target=\"_blank\" class=\"link_dest\">平山</a>客运站\r\n			</p>\r\n			<p>\r\n				保定客运总站——<a href=\"http://travel.qunar.com/p-cs298944-pingshan\" target=\"_blank\" class=\"link_dest\">平山</a>客运站\r\n			</p>\r\n			<p>\r\n				<a href=\"http://travel.qunar.com/p-cs298944-pingshan\" target=\"_blank\" class=\"link_dest\">平山</a>客运站——沕沕水，上午8:30，&nbsp;下午1:20。一天中有两趟车到沕沕水景区。\r\n			</p>\r\n			<p>\r\n				<a href=\"http://travel.qunar.com/p-cs298944-pingshan\" target=\"_blank\" class=\"link_dest\">平山</a>客运站有出租车和私家车到沕沕水景区，最多100元，但回城车不好打，只能坐班车。\r\n			</p>\r\n			<p>\r\n				一日游新路：\r\n			</p>\r\n			<p>\r\n				旅行社直通车（天天发）：\r\n			</p>\r\n			<p>\r\n				6：50省博物馆东门；7:10<a href=\"http://travel.qunar.com/p-oi10012365-xiqinggongyuan\" target=\"_blank\" class=\"link_dest\">西清公园</a>北门；7:30<a href=\"http://travel.qunar.com/p-oi704799-shuishanggongyuan\" target=\"_blank\" class=\"link_dest\">水上公园</a>西门。\r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', '25', '交通');
INSERT INTO `youjidiv` VALUES ('390', '', '<p>\r\n	<p class=\"first\">\r\n		农家乐：景区门口有很多农家乐，几十块钱。\r\n	</p>\r\n	<p>\r\n		宾馆：沕沕水宾馆，就在景区门口，景区条件最好的，豪华标间300元，含双早，送两张景区门票。去哪网可以预订。电话预订也可以。电话0311-82890027\r\n	</p>\r\n	<p>\r\n		<img src=\"https://img1.qunarzz.com/travel/d2/1610/4a/49a140e92a6133b5.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n	</p>\r\n</p>', '25', '平山沕沕水宾馆');
INSERT INTO `youjidiv` VALUES ('391', '沕沕水景区', '<div class=\"text js_memo_node\">\r\n	<p class=\"first\">\r\n		景点简介：\r\n	</p>\r\n	<p>\r\n		春赏山花、夏看瀑布、秋观红叶、冬览冰瀑。<a href=\"http://travel.qunar.com/p-oi7469490-wuwushuishengtaifengjing\" target=\"_blank\" class=\"link_dest\">沕沕水生态风景区</a>（沕沕水：当地人念做&nbsp;hū&nbsp;hū水，但现代字典中称“沕”读作“mì”或“wù”，在辞海中可以查到“&nbsp;hū&nbsp;”的音）位于革命老区河北省<a href=\"http://travel.qunar.com/p-cs298944-pingshanxian\" target=\"_blank\" class=\"link_dest\">平山县</a>西南边缘，距河北省省会<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>95公里。现为国家4A景区、国家水利风景区、国家风景名胜区和中国最佳生态旅游景区。该景区集自然风光、人文景观和红色旅游于一体，品味高雅、特色鲜明、风情浓郁、倍受游客青睐。早在明清时代，即为平山“八大胜景”之一，素享“沕水瀑布天上降”的盛誉。是河北境内四季均可游览的景区之一。门票：65元。\r\n	</p>\r\n	<p>\r\n		2016年1月16日，早客运北站（紧邻火车北站）乘车，行驶一小时到达平山客运站。到达后可以逛逛县城，吃点平山特色小吃后乘车前往沕沕水。客运站门口也有很多出租车和私家车，60公里，最多100元即可。\r\n	</p>\r\n	<p>\r\n		到达沕沕水后入住沕沕水宾馆，送两张门票。\r\n	</p>\r\n	<p>\r\n		宾馆餐厅午餐后，稍作休息，前往景区。\r\n	</p>\r\n</div>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d5/1610/27/c6d8bc050ab34cb5.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d3/1610/f8/ea9b9cac17c622b5.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d1/1610/14/df024e52e539d4b5.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>', '25', '沕沕水景区');
INSERT INTO `youjidiv` VALUES ('392', '前言', '<p class=\"first\">\r\n	印象中的<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>是什么样呢？\r\n</p>\r\n<p>\r\n	在大家眼中，<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>可能和旅游城市挂不上钩吧\r\n</p>\r\n<p>\r\n	但是今天就是让你大跌眼镜的时候！！！\r\n</p>\r\n<p>\r\n	敲重点！！！不一样的国际庄，你值得拥有！\r\n</p>\r\n<p>\r\n	今天大哥给大家介绍一下最值得去的几个景点！！！\r\n</p>\r\n<p>\r\n	六个小众又不烂大街的景点，包你满意~\r\n</p>', '26', '石家庄');
INSERT INTO `youjidiv` VALUES ('393', '交通全知道', '<div class=\"e_img_schedule\">\r\n	<div class=\"imglst\">\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				地铁：<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>共有两条地铁在运营，所以地铁出行各个景点不是太方便！所以不太推荐！\r\n			</p>\r\n			<p>\r\n				公交：四通八达而且Nice的出行方式！但是远途的乘客不推荐！太累啦！\r\n			</p>\r\n			<p>\r\n				网约车：滴滴是很方便的出行方式，性价比非常不错。现在还有优惠链接（拿好不送）<a target=\"_blank\" href=\"https://dc.tt/stC3U6khMTYlj\">https://dc.tt/stC3U6khMTYlj</a>\r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', '26', '石家庄站');
INSERT INTO `youjidiv` VALUES ('394', '景点大搜罗', '<p>\r\n	<a href=\"http://travel.qunar.com/p-oi705871-baoduzhai\" target=\"_blank\" class=\"link_dest\">抱犊寨</a>，旧名抱犊山，古名萆山，位于河北省<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>鹿泉市西郊，是一处集历史人文和自然风光为一体的名山古寨\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d3/1807/89/77cbc312d4faa8b5.gif\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	系国家4A级旅游区、河北省旅游先进单位、<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>十佳旅游景区。东临华北平原，西接太行群峰，一峰突起，峥嵘雄秀，四周皆是悬崖绝壁，远望犹如巨佛仰卧，眉目毕肖，其山顶平旷坦夷，有良田沃土660亩，土层深达66米，异境别开，草木繁茂，恍如世外桃源。<img src=\"https://img1.qunarzz.com/travel/d0/1807/39/d6981d766ceacab5.gif\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>', '26', '抱犊寨');
INSERT INTO `youjidiv` VALUES ('395', '', '<div class=\"text js_memo_node\">\r\n	<p class=\"first\">\r\n		新晋打卡炒火的漫山花溪谷！！！\r\n	</p>\r\n	<p>\r\n		来过的都叫好！\r\n	</p>\r\n</div>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d3/1807/df/8c69721fe00538b5.jpg_r_680x453x95_d3b4a2ef.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	漫山花溪谷位于<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>西北110公里太行深山区，毗邻五岳寨国家森林公园，是驼梁景区的必经之地，全长12.8公里，总面积20平方公里，与五岳寨主峰相通相连。\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d6/1807/3f/1d4f1deda1538eb5.jpg_r_680x453x95_33bb46f5.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	谷内森林、山泉、草原、花园等自然资源得天独厚，10000亩原始次生林浓密葱郁，两条连天瀑布似白练垂空，顶端亚高原草甸广阔无垠，20万平米山地花园明媚光艳，一条清溪流贯全谷，繁花点缀溪旁，“花溪谷”故此得名<img src=\"https://img1.qunarzz.com/travel/d1/1807/d5/ae5d6200429d89b5.jpg_r_680x446x95_0530e6a3.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>', '26', '漫山花溪谷');
INSERT INTO `youjidiv` VALUES ('396', '', '<p>\r\n	<p class=\"first\">\r\n		北靠迎宾大道、东临中华大街。由1栋海悦国际俱乐部、2栋EOD花园写字楼、3栋LOFT行政公馆，15万㎡威尼斯购物公园构成。\r\n	</p>\r\n	<p>\r\n		金正·海悦天地四层引入华北首条180米威尼斯风情水街，负一层建造大型室内游乐设施----1000米迪斯尼动力火车&nbsp;&nbsp;线，将游乐功能与休闲购物融为一体，中国首席游乐购物综合体！\r\n	</p>\r\n	<p>\r\n		<img src=\"https://img1.qunarzz.com/travel/d8/1807/ef/1da46020dc8ebbb5.jpg_r_680x451x95_ff372016.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n	</p>\r\n	<p>\r\n		一窥海悦天地风采，感受泛舟水上的全新炫彩生活方式。海悦天地组成了一个集购物、休闲、美食、娱乐为一体的情景式体验购物中心。全维度感官在这里都能得到最大程度的满足，视觉、味觉、嗅觉、触觉、听觉都是您融入水上威尼斯风情的通路，省会首家游乐购主题SHOPPING&nbsp;MALL，一定会给您带来异域风情的一站式游乐购物新体验。\r\n	</p>\r\n	<p>\r\n		<img src=\"https://img1.qunarzz.com/travel/d6/1807/b0/1df614f8aeadb3b5.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n	</p>\r\n</p>', '26', '海悦天地购物广场');
INSERT INTO `youjidiv` VALUES ('397', '', '<p>\r\n	<a href=\"http://travel.qunar.com/p-oi9498526-shijiazhuanghaiyanggong\" target=\"_blank\" class=\"link_dest\">石家庄海洋公园</a>坐落于海悦天地西侧负一层，总建筑面积约1万平方米，是目前为止<a href=\"http://travel.qunar.com/p-cs300117-shijiazhuang\" target=\"_blank\" class=\"link_dest\">石家庄</a>首座室内海洋馆，拥有近1000种，约10000只极地海洋动物及十几个主题展馆，营造了集观赏、娱乐、休闲、科普、环保于一体的梦幻主题式极地海洋体验。\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d6/1807/75/02fdfbeaee7571b5.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<a href=\"http://travel.qunar.com/p-oi9498526-shijiazhuanghaiyanggong\" target=\"_blank\" class=\"link_dest\">石家庄海洋公园</a>包括：淡水世界、企鹅馆、锦鲤区、鲟鱼馆、海豹馆、水下表演、探索池、海洋鱼馆、历史长廊、交互空间、水母宫、珊瑚馆、陈列馆、海底隧道、童乐馆等展示项目。360°超大广角海底隧道：你可以360°感受鱼群从你四周游过，海底美景触手可及，海底沉覆玛雅文化再度重现，为你展示真实的海底奇观。梦幻主题海洋餐厅，以海为幕，与鱼为邻，一边与窗边悠然游过的海洋生物打招呼，一边体验新奇梦幻的味觉盛宴。\r\n</p>', '26', '海悦天地海洋馆');
