INSERT INTO `youjidiv` VALUES ('400', '', '<div class=\"e_img_schedule\">\r\n	<div class=\"imglst\">\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				或许都有在寂寞的夜幻想着逃离的经历\r\n			</p>\r\n			<p>\r\n				毕竟在帝都压力太大，\r\n			</p>\r\n			<p>\r\n				总是会让人产生恍惚之感\r\n			</p>\r\n			<p>\r\n				也会有无数次想要在深夜裸辞，去寻找诗和远方\r\n			</p>\r\n			<p>\r\n				却因为现实而迫于无奈呆在这里\r\n			</p>\r\n			<p>\r\n				那么就去旅行吧，放空自己\r\n			</p>\r\n			<p>\r\n				去<a href=\"http://travel.qunar.com/p-cs299957-tianjin\" target=\"_blank\" class=\"link_dest\">天津</a>，感受另一种悠闲的生活！！！\r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', '27', '说说这次旅行');
INSERT INTO `youjidiv` VALUES ('401', '', '<div id=\"day-20001202\" class=\"e_day day_style0 day-20001202\">\r\n	<div class=\"period_ct\">\r\n		<div class=\"b_poi_info b_poi_item b_poi_info_hover\" id=\"ele-2927564-2\">\r\n			<div class=\"bottom\">\r\n				<div class=\"e_img_schedule\">\r\n					<div class=\"imglst\">\r\n						<div class=\"text js_memo_node\">\r\n							<p class=\"first\">\r\n								旅游必备物品准备清单如下：&nbsp;\r\n							</p>\r\n							<p>\r\n								一、证件：身份证、学生证、车票、现金、信用卡&nbsp;&nbsp;\r\n							</p>\r\n							<p>\r\n								二、用品：手机（确认手机费充足）、相机、充电器、遮阳伞、湿巾、卷纸面巾纸&nbsp;&nbsp;\r\n							</p>\r\n							<p>\r\n								三、药品：风油精、藿香正气、创可贴、晕车药、眼药水&nbsp;&nbsp;、健胃消食片、胃药\r\n							</p>\r\n							<p>\r\n								四、衣物：衣裤&nbsp;2套，内衣裤（内裤可多带）、轻便运动休闲鞋、拖鞋、棉袜、泳衣泳镜、太阳帽、小背包（短途出行时用，放随身小东西用）&nbsp;&nbsp;\r\n							</p>\r\n							<p>\r\n								五、洗漱用品：牙刷牙膏、毛巾、护肤品、洗发水、沐浴露、防晒油\r\n							</p>\r\n							<p>\r\n								总结：也可以适量减少！轻装上阵！\r\n							</p>\r\n						</div>\r\n					</div>\r\n				</div>\r\n				<div class=\"ops\">\r\n					<div class=\"inner clrfix\">\r\n						<div class=\"left\">\r\n						</div>\r\n						<div class=\"right\">\r\n							<a class=\"comment\">评论<i></i></a>\r\n						</div>\r\n					</div>\r\n				</div>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>\r\n<div id=\"day-20001203\" class=\"e_day day_style0 day-20001203\">\r\n	<h4 class=\"period_hd\">\r\n	</h4>\r\n</div>', '27', '行前准备');
INSERT INTO `youjidiv` VALUES ('402', '第1天', '<p class=\"first\">\r\n	从北京到<a href=\"http://travel.qunar.com/p-cs299957-tianjin\" target=\"_blank\" class=\"link_dest\">天津</a>很方便\r\n</p>\r\n<p>\r\n	基本上半个小时的城际列车就到了\r\n</p>\r\n<p>\r\n	不需要太早在网上预订\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d3/1808/4c/5be030ad8b45bab5.jpg_r_680x106x95_8c24496c.jpg\" width=\"400\" height=\"200\" alt=\"\" />\r\n</p>', '27', '天津站');
INSERT INTO `youjidiv` VALUES ('403', '', '<p>\r\n	一下车就可以开始第一天的游玩啦，第一站先去<a href=\"http://travel.qunar.com/p-oi5740240-jinwanguangchang\" target=\"_blank\" class=\"link_dest\">津湾广场</a>。\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d6/1808/fb/718a999bae8f00b5.jpg_r_680x509x95_4cfcd8a0.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<a href=\"http://travel.qunar.com/p-oi5740240-jinwanguangchang\" target=\"_blank\" class=\"link_dest\">津湾广场</a>的周边有几处著名的看点，解放路曾是百年前的法租界，这里有大量优雅的法国建筑，沿河而建，非常漂亮；横跨河上的解放桥已经有一百多年的历史了，桥对岸的世纪钟更是天津的著名地标；<a href=\"http://travel.qunar.com/p-oi5420141-tianjinzhan\" target=\"_blank\" class=\"link_dest\">天津站</a>及其背靠的高楼则是一派繁华的现在市景，夜晚时亮灯之后绚烂迷人。来<a href=\"http://travel.qunar.com/p-oi5740240-jinwanguangchang\" target=\"_blank\" class=\"link_dest\">津湾广场</a>参观和拍摄夜景如今是很多游人游玩天津时必做事项，若是情侣出行，更要在这里漫步，感受<a href=\"http://travel.qunar.com/p-oi10067310-jinmen\" target=\"_blank\" class=\"link_dest\">津门</a>夜晚的浪漫。\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d3/1808/1a/06da3a1067fa3bb5.jpg_r_680x510x95_cade3308.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<div class=\"text js_memo_node\">\r\n		<p class=\"first\">\r\n			从<a href=\"http://travel.qunar.com/p-oi5740240-jinwanguangchang\" target=\"_blank\" class=\"link_dest\">津湾广场</a>附近转完就可以去<a href=\"http://travel.qunar.com/p-oi705620-yidalifengqingjie\" target=\"_blank\" class=\"link_dest\">意大利风情街</a>了\r\n		</p>\r\n		<p>\r\n			天津交通相比北京而言要好很多，所以可以选择多种出行方式。\r\n		</p>\r\n	</div>\r\n</p>', '27', '津湾广场');
INSERT INTO `youjidiv` VALUES ('404', '', '<p>\r\n	基本上午饭就可以在<a href=\"http://travel.qunar.com/p-oi705620-yidalifengqingqu\" target=\"_blank\" class=\"link_dest\">意大利风情区</a>解决，<a href=\"http://travel.qunar.com/p-oi705620-yidalifengqingqu\" target=\"_blank\" class=\"link_dest\">意大利风情区</a>非常适合拍照，在这里坐一坐感受一下小资，享受一下生活也未尝不是一种放松方式。\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d0/1808/4f/eda78c5cb89a94b5.jpg_r_680x453x95_1a344653.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<a href=\"http://travel.qunar.com/p-oi705620-yidalifengqingqu\" target=\"_blank\" class=\"link_dest\">意大利风情区</a>位于<a href=\"http://travel.qunar.com/p-cs299957-tianjinshi\" target=\"_blank\" class=\"link_dest\">天津市</a>中心，在这里有200余栋地中海风格的典雅建筑。漫步其中，就好像行走在浪漫的欧洲小镇，众多的名人故居、影视剧取景地也为游人的旅程增色不少\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d6/1808/48/eb9a8e529b8caeb5.jpg_r_680x453x95_ef9d33f4.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>', '27', '意大利风情区');
INSERT INTO `youjidiv` VALUES ('405', '', '<p class=\"first\">\r\n	吃完饭稍微休息一下，等太阳小一些了就可以去下一站<a href=\"http://travel.qunar.com/p-oi702210-guwenhuajie\" target=\"_blank\" class=\"link_dest\">古文化街</a>了\r\n</p>\r\n<p>\r\n	<a href=\"http://travel.qunar.com/p-oi702210-guwenhuajie\" target=\"_blank\" class=\"link_dest\">古文化街</a>位于<a href=\"http://travel.qunar.com/p-cs299957-tianjinshi\" target=\"_blank\" class=\"link_dest\">天津市</a>南开区，以天后宫为中心，南起水阁大街，北到通北路。这里是天津最著名的老字号和手工艺品店铺集中地。可以找到杨柳青年画、泥人张、魏记风筝等众多的手工艺品，还能品尝到经典的天津美食。\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d2/1808/2d/ca9eb04eea95cfb5.jpg_r_680x509x95_66288bf0.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d7/1808/6b/fb640bf15f8f74b5.jpg_r_680x510x95_ca74db79.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>', '27', '古文化街');
INSERT INTO `youjidiv` VALUES ('406', '', '<p class=\"first\">\r\n	<a href=\"http://travel.qunar.com/p-cs299957-tianjin\" target=\"_blank\" class=\"link_dest\">天津</a>的好吃非常的多，<a href=\"http://travel.qunar.com/p-oi702210-guwenhuajie\" target=\"_blank\" class=\"link_dest\">古文化街</a>走一圈基本上就已经吃的很饱了。\r\n</p>\r\n<p>\r\n	吃完饭去<a href=\"http://travel.qunar.com/p-oi5144697-tianjinzhiyan\" target=\"_blank\" class=\"link_dest\">天津之眼</a>玩儿，感受别样的浪漫。\r\n</p>\r\n<p>\r\n	<a href=\"http://travel.qunar.com/p-oi5144697-tianjinzhiyan\" target=\"_blank\" class=\"link_dest\">天津之眼</a>每晚6点以后才开，俯瞰<a href=\"http://travel.qunar.com/p-cs299957-tianjin\" target=\"_blank\" class=\"link_dest\">天津</a>夜景，感觉非常好。\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d1/1808/28/b4432d01566275b5.jpg_r_680x453x95_4fac2ace.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d3/1808/4d/b14ddbceb4a6afb5.jpg_r_680x369x95_0cc012ac.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>', '27', '天津之眼摩天轮');
INSERT INTO `youjidiv` VALUES ('407', '第2天', '<p>\r\n	第二天早起坐车去<a href=\"http://travel.qunar.com/p-oi5229782-wudadao\" target=\"_blank\" class=\"link_dest\">五大道</a>\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d8/1808/56/beaa8757597490b5.jpg_r_481x680x95_80608c68.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>', '27', '五大道');
INSERT INTO `youjidiv` VALUES ('408', '前言', '<p class=\"first\">\r\n	<br />\r\n</p>', '28', '上海');
INSERT INTO `youjidiv` VALUES ('409', '', '<p class=\"first\">\r\n	<a href=\"https://travel.qunar.com/p-cs299878-shanghai\" target=\"_blank\" class=\"link_dest\">上海</a>的周末做什么\r\n</p>\r\n<p>\r\n	寒冷的天气做什么\r\n</p>\r\n<p>\r\n	那就去看展吧\r\n</p>\r\n<p>\r\n	<img src=\"https://tr-osdcp.qunarzz.com/tr-osd-tr-space/img/0d2837f5a007f9994d4c9ab3b92a972f.jpg\" width=\"200\" height=\"200\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<p>\r\n		给大家推荐几个拍照超美的展览馆\r\n	</p>\r\n	<p>\r\n		在艺术的熏陶下\r\n	</p>\r\n	<p>\r\n		还能拍出美美的照♥♥\r\n	</p>\r\n</p>', '28', '说说这次旅行');
INSERT INTO `youjidiv` VALUES ('410', '上海玻璃博物馆', '<p class=\"first\">\r\n	<a href=\"https://travel.qunar.com/p-oi7643882-shanghaibolibowuguan\" target=\"_blank\" class=\"link_dest\">上海玻璃博物馆</a>坐落于宝山区长江西路\r\n</p>\r\n<p>\r\n	这曾是<a href=\"https://travel.qunar.com/p-cs299878-shanghai\" target=\"_blank\" class=\"link_dest\">上海</a>轻工玻璃公司的玻璃窑炉车间，改建成博物馆\r\n</p>\r\n<p>\r\n	外观简单内部时尚大气,极具空间感。\r\n</p>\r\n<p>\r\n	在这里你可以欣赏各种各样有特色的玻璃制品\r\n</p>\r\n<p>\r\n	还<a href=\"https://travel.qunar.com/p-oi9781022-keyiguan\" target=\"_blank\" class=\"link_dest\">可以观</a>赏师傅现场表演的玻璃制品制作过程\r\n</p>\r\n<p>\r\n	最重要的还可以亲手DIY玻璃制品，做一件属于自己的艺术品\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	地址:&nbsp;\r\n</p>\r\n<p>\r\n	<a href=\"https://travel.qunar.com/p-cs299878-shanghai\" target=\"_blank\" class=\"link_dest\">上海</a>市宝山区长江西路685号\r\n</p>\r\n<p>\r\n	开放时间:\r\n</p>\r\n<p>\r\n	5月、6月、9月、10月周二-周五9:30-17:00\r\n</p>\r\n<p>\r\n	周六、周日节假日及7月、8月(周二-周日)9:30-18:00(周六夜场：17:00-21:00)\r\n</p>\r\n<p>\r\n	周一不开放，如遇法定节假日博物馆将照常开放；闭馆前60分钟停止购票，闭馆前30分钟停止入场\r\n</p>\r\n<p>\r\n	√√去哪儿官网购票链接：<a target=\"_blank\" href=\"http://piao.qunar.com/ticket/detaillight_39212.html#in_track=travel_place\">http://piao.qunar.com/ticket/detaillight_39212.html#in_track=travel_place</a>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"https://tr-osdcp.qunarzz.com/tr-osd-tr-space/img/fb6d9cbdd9ba6358c755f2d8676944c7.gif_r_680x450x95_7a5f06f8.gif\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"https://tr-osdcp.qunarzz.com/tr-osd-tr-space/img/c5e2fa8832a2e7a20bdc5894f63a863b.gif_r_680x436x95_34c7cb29.gif\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>', '28', '上海玻璃博物馆');
INSERT INTO `youjidiv` VALUES ('411', '上海当代艺术馆', '<p class=\"first\">\r\n	<a href=\"https://travel.qunar.com/p-oi10006306-shanghaidangdaiyishuguan\" target=\"_blank\" class=\"link_dest\">上海当代艺术馆</a>（MOCA&nbsp;Shanghai）座落于市中心的<a href=\"https://travel.qunar.com/p-oi704564-renmingongyuan\" target=\"_blank\" class=\"link_dest\">人民公园</a>内\r\n</p>\r\n<p>\r\n	展览除了纯艺术的展览也有贴近大众的品牌展、设计展\r\n</p>\r\n<p>\r\n	艺术馆是由龚明光基金会出资建造，经<a href=\"https://travel.qunar.com/p-cs299878-shanghaishi\" target=\"_blank\" class=\"link_dest\">上海市</a>政府批准的非盈利且独立运作的艺术机构\r\n</p>\r\n<p>\r\n	地址:&nbsp;<a href=\"https://travel.qunar.com/p-cs299878-shanghaishi\" target=\"_blank\" class=\"link_dest\">上海市</a>黄浦区<a href=\"https://travel.qunar.com/p-oi3540029-nanjingxilu\" target=\"_blank\" class=\"link_dest\">南京西路</a>231号<a href=\"https://travel.qunar.com/p-oi704564-renmingongyuan\" target=\"_blank\" class=\"link_dest\">人民公园</a>7号门内\r\n</p>\r\n<p>\r\n	网址：<a target=\"_blank\" href=\"http://www.mocashanghai.org/\">http://www.mocashanghai.org/</a>\r\n</p>\r\n<p>\r\n	开放时间:&nbsp;\r\n</p>\r\n<p>\r\n	10:00-18:00（17:00停止售票）\r\n</p>\r\n<p>\r\n	<img src=\"https://tr-osdcp.qunarzz.com/tr-osd-tr-space/img/e480b8838e7944fddbdeb708bc26172f.gif_r_680x510x95_20acc78d.gif\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"https://tr-osdcp.qunarzz.com/tr-osd-tr-space/img/050191fb84df53e3ffa89335a7cfae6e.jpg_r_680x350x95_98065e5e.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>', '28', '上海当代艺术馆');
INSERT INTO `youjidiv` VALUES ('412', '宝龙美术馆', '<p class=\"first\">\r\n	<a href=\"https://travel.qunar.com/p-cs299878-shanghai\" target=\"_blank\" class=\"link_dest\">上海</a>宝龙美术馆占地面积23000平方米，拥有500至1100平方米的大小展厅共十个\r\n</p>\r\n<p>\r\n	以探寻东方文脉为核心学术定位\r\n</p>\r\n<p>\r\n	美术馆推出“书藏楼珍藏展”、“宝龙美术馆开馆展“两个展览\r\n</p>\r\n<p>\r\n	“书藏楼珍藏展”主要收藏近现代及当代全国书画名家的存世精品\r\n</p>\r\n<p>\r\n	展出齐白石、吴湖帆、黄永玉、黄胄等百余件名家名作\r\n</p>\r\n<p>\r\n	地址:&nbsp;\r\n</p>\r\n<p>\r\n	<a href=\"https://travel.qunar.com/p-cs299878-shanghai\" target=\"_blank\" class=\"link_dest\">上海</a>市闵行区漕宝路3055号（近新镇路）\r\n</p>\r\n<p>\r\n	官网:&nbsp;<a target=\"_blank\" href=\"http://www.powerlongmuseum.com/\">http://www.powerlongmuseum.com/</a>\r\n</p>\r\n<p>\r\n	开放时间:&nbsp;\r\n</p>\r\n<p>\r\n	10:00-18:00，17:00后停止进场，周一闭馆\r\n</p>\r\n<p>\r\n	<img src=\"https://tr-osdcp.qunarzz.com/tr-osd-tr-space/img/6a73d4ce412433d0c772113caab5835c.jpg_r_680x453x95_e4474b85.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"https://tr-osdcp.qunarzz.com/tr-osd-tr-space/img/72b5dba856aa4c791f538aff75a5a223.jpg_r_680x404x95_f7a0d96c.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>', '28', '宝龙美术馆');
INSERT INTO `youjidiv` VALUES ('413', 'Finally', '<div class=\"e_img_schedule\">\r\n	<div class=\"imglst\">\r\n		<div class=\"text js_memo_node\">\r\n			<p class=\"first\">\r\n				大家可以都去查查看每个展览馆官网现在可以找到自己感兴趣的展览去看\r\n			</p>\r\n			<p>\r\n				喝喝咖啡，看看展\r\n			</p>\r\n			<p>\r\n				不就是在寒冷的天气下\r\n			</p>\r\n			<p>\r\n				最惬意和最棒的选择吗?\r\n			</p>\r\n			<p>\r\n				希望大家都能找到自己喜欢看的展\r\n			</p>\r\n			<p>\r\n				度过美好的每一天~~~~~\r\n			</p>\r\n			<p>\r\n				end....\r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', '28', '');
