INSERT INTO `youjidiv` VALUES ('441', '', '<p>\r\n	20:00到达<a href=\"http://travel.qunar.com/p-cs299957-tianjin\" target=\"_blank\" class=\"link_dest\">天津</a>宝轩酒店，这么快，早知道不在唐官屯服务区吃饭了，节省45分钟，酒店对面就有一家很好的快餐店，以后我们在那家店吃了好几顿，味道不错的。\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d3/1704/38/01d136f4ad1405b5.jpg_r_382x680x95_1eb79b6b.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>', '32', '天津宝轩大酒店');
INSERT INTO `youjidiv` VALUES ('442', '', '<p>\r\n	0月1日，从合肥到<a href=\"http://travel.qunar.com/p-cs299957-tianjin\" target=\"_blank\" class=\"link_dest\">天津</a>951公里，发动机运转10小时17分钟，百公里平均油耗6.6，平均时速93公里。一路往北都是限速120（除了一小段100），一个隧道都没有，才能跑出这个速度，才能跑出这个公里数。951公里，是截止目前我一天自驾最多的里程，希望将来的某一天能够突破1000公里！\r\n</p>\r\n<p>\r\n	<img src=\"https://img1.qunarzz.com/travel/d9/1704/ee/dce3542e04170ab5.jpg\" width=\"400\" height=\"300\" alt=\"\" />\r\n</p>', '32', '简单总结');
